import { useState } from 'react'
import './App.css'

function App() {
  const [image, setImage] = useState('')
  const image1 = "data:image/jpeg;base64,"
  const image2 = "data:image/jpeg;base64," 
  const image3 = "data:image/jpeg;base64,"

  const imageArr = [image1,image2,image3]

  function handleImageClick(imageChange){
    setImage(imageChange)
  }

  return (
    <>
    <div class='a'>
          {imageArr.map((img,index)=>(
            <img key={index} src={img} alt={img}  style={{cursor:'pointer'}} onClick={()=>handleImageClick(img)}
            />
          ))
        }
     </div>

      <div class="clickedImage">
        {image ?
        <>
         <p>Selected Image</p>
         <img src={image} alt="selected" /> 
        </>
       : null}
      </div>
    </>
  )
}

export default App
